		AND roleName = #{args.roleName, jdbcType=VARCHAR} 
	
	
		AND menuQx = #{args.menuQx, jdbcType=VARCHAR} 
	
	
		AND nickname = #{args.nickname, jdbcType=VARCHAR} 
	
	
