SELECT menuId, parentId, menuName, permissions, menuUrl, icon, menuType, menuFunc, visible, menuId spid FROM sys_menu WHERE `status` = 1 AND menuFunc != 3
AND parentId = #{_parameter, jdbcType=INTEGER}
ORDER BY sort ASC

SELECT FROM sys_menu WHERE `status` = 1 AND visible = 1
AND parentId = #{_parameter, jdbcType=INTEGER}
ORDER BY sort ASC

SELECT menuId, parentId, menuName, permissions, menuUrl, icon, menuType, menuFunc, sort, visible, checkValue FROM sys_menu
WHERE `status` = 1 AND parentId = #{parentId, jdbcType=INTEGER}
ORDER BY sort ASC
